import React, { Component } from 'react';

export class EventHandler extends Component {
    constructor(){
        super(props);
        this.state={
            isToggleOn : true
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(){
        this.setState(state=>({
            isToggleOn : !isToggleOn
        }));
    }

    render() {
        return (
            <div>

                <button onClick = {this.handleClick}>
                    {isToggleOn?'ON':'OFF'}
                </button>

            </div>
        )
    }
}

export default EventHandler
