import React from 'react';

class Toggle extends React.Component{

    constructor(){
        super(props);
        this.state={
            isToggleOn:true
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(){
        this.setState(state=>({
            isToggleOn:!state.isToggleOn
        }));
    }

    render(){

        return(
            <button>
                {this.state.isToggleOn?'ON':'OFF'}
            </button>

        );
    }

}

export default EvenHandler
